import React, {useState, useEffect} from "react"; 
//import './App.css';
import Axios from 'axios';

import { useAuth } from "./auth";
import {BrowserRouter as Router, Switch, Route, Link, useNavigate, useParams} from 'react-router-dom';


function CarsForRent() {
  
  const [data, setData] = useState([])

  const [minPriceFilter, setMinPriceFilter] = useState("");
  const [maxPriceFilter, setMaxPriceFilter] = useState("");
  const [type, setType] = useState("");


  const minPrice = (value) => parseInt(value.price) >= minPriceFilter; 
  const maxPrice = (value) => parseInt(value.price) <= maxPriceFilter; 
  const carType = (value) => value.type === type; 
  
    
   

  const info = async () => {
    

    Axios.post("http://localhost:3001/api/Cars", {
    
  }).then((response) => {
    let tempData = response.data
    //setData(response.data)
    for(const car of response.data){
      console.log(car.price + minPriceFilter)

      if(minPriceFilter == ""){
        
      }
      else{
        /*let tempOptions = data;
        (response.data).map((value, index) => {  // consider array has some data
            tempOptions[index] = (response.data).filter(minPrice) ;
        });
        setData(tempOptions);*/
        tempData = tempData.filter(minPrice)
        
      }
      

      if(maxPriceFilter == ""){
        
        
      }
      else{
       /* let tempOptions = data;
        (response.data).map((value, index) => {  // consider array has some data
            tempOptions[index] = (response.data).filter(maxPrice) ;
        });
        setData(tempOptions);*/
        tempData = tempData.filter(maxPrice)
        
      }

      if(type === "all"){
        
        
      }
      else{
       /* let tempOptions = data;
        (response.data).map((value, index) => {  // consider array has some data
            tempOptions[index] = (response.data).filter(maxPrice) ;
        });
        setData(tempOptions);*/
       
        tempData = tempData.filter(carType)
      }
      
      
      
    }
    setData(tempData)
    
  })}
  useEffect(() => {
    

    info()
    
  })

  const MoreInfo = () => {
    {info()}
  }
  


  return (
      // <Router> 
      <><div>
      
    </div>
      
        <h1> RentMyRide </h1>
        <h1> {minPriceFilter} </h1>
        
        <div className="MinimumPrice">
          <input type="number" placeholder="Minimum price" name="priceFilter"  onChange={(e)=>{setMinPriceFilter(e.target.value)}} />
        </div>
        <div className="MaximumPrice">
          <input type="number" placeholder="Maximum price" name="priceFilter"  onChange={(e)=>{setMaxPriceFilter(e.target.value)}} />
        </div>
        <div className="Type">
          <select name="type" placeholder="type"  onChange={(e)=>{setType(e.target.value)}}>
          <option value="all">all</option>
            <option value="SUV">SUV</option>
            <option value="Sedan">Sedan</option>
            <option value="Truck">Truck</option>
            <option value="Coupe">Coupe</option>
            <option value="Other">Other</option>
          </select>
        </div>
        



        {
          
          data.map( (getD)=>(

            
            <div key={getD.regNumber}>
              <div className = "Car">
              <a href={"/CarDetails/"+getD.regNumber}>{getD.make} {getD.model}</a>
              <br></br>
                <div className="Price"><strong>Price: </strong>{  getD.price  } per day</div>
                <br></br>
                <div className="Picture"><strong></strong><img src={require(`../imgs/${  getD.photo  }`)} width="300" height="215" /> </div>
                <br></br>
                <br></br>
              </div>
              <br>
              
              </br>
                
            
            </div>
            

          )
          
          )
        }

      
      </>
  );}



export default CarsForRent;
