import React, { useState } from 'react'


import axios from "axios"
import {useNavigate} from "react-router-dom"

const Register = () => {

    const [fname,setFName] = useState("");
    const [file,setFile] = useState("");

    const history = useNavigate();

    const setdata = (e)=>{
        setFName(e.target.value)
    }

    const setimgfile = (e)=>{
        setFile(e.target.files[0])
    }

    const addUserData = async(e)=>{
        e.preventDefault();

        var formData = new FormData();
        formData.append("photo",file)
        formData.append("fname",fname);

        const config = {
            headers:{
                "Content-Type":"multipart/form-data"
            }
        }

        // axios.post("http://localhost:3001/register",formData,config).then((response) => {
        //     console.log(response)
        //   })
       
          const res = await axios.post("http://localhost:3001/register",formData,config);
       
          if(res.data.status == 201){
              history("/")
          }else{
              console.log("error")
          }
      
    }

    return (
        <>
            <div className='container mt-3'>
                <h1>Upload Your Img Here</h1>

                {/* <Form> */}
                    {/* <Form.Group className="mb-3" controlId="formBasicEmail"> */}
                        <div>UserName</div>
                        <input type="text" name='fname' onChange={setdata} />
                    {/* </Form.Group> */}

                    {/* <Form.Group className="mb-3" controlId="formBasicPassword"> */}
                        <div>Select Your Image</div>
                        <input type="file" name='photo' onChange={setimgfile} />
                    {/* </Form.Group> */}
                    <button variant="primary" type="submit" onClick={addUserData}>
                        Submit
                    </button>
                {/* </Form> */}
            </div>
        </>
    )
}

export default Register
