import React, {useState, useEffect} from "react"; 
//import './App.css';
import Axios from 'axios';

import { useAuth } from "./auth";
import {BrowserRouter as Router, Switch, Route, Link, useNavigate, useParams} from 'react-router-dom';


function CarsForRent() {
  
  const [data, setData] = useState([])

  const [minPriceFilter, setMinPriceFilter] = useState("0");
  const [maxPriceFilter, setMaxPriceFilter] = useState("10000");

  const minPrice = (value) => value.price >= minPriceFilter; 
  const maxPrice = (value) => value.price <= maxPriceFilter; 
    
   

  const info = async () => {
    

    Axios.post("http://localhost:3001/api/Cars", {
    
  }).then((response) => {
    setData(null)
    for(const car of response.data){

      if(minPriceFilter == 0){
        setData(response.data)
      }
      else{
        let tempOptions = data;
        (response.data).map((value, index) => {  // consider array has some data
            tempOptions[index] = (response.data).filter(minPrice) ;
        });
        setData(tempOptions);
        setData((response.data).filter(minPrice))
        
      }

      /*if(maxPriceFilter == 10000){
        setData(response.data)
      }
      else{
        let tempOptions = data;
        (response.data).map((value, index) => {  // consider array has some data
            tempOptions[index] = (response.data).filter(maxPrice) ;
        });
        setData(tempOptions);
        setData((response.data).filter(maxPrice))
        
      }*/
      
      
      
    }
    
  })}
  useEffect(() => {
    

    info()
    
  })

  const MoreInfo = () => {
    {info()}
  }
  


  return (
      // <Router> 
      <><div>
      
    </div>
      
        <h1> RentMyRide </h1>
        
        
        <input type="number" placeholder="Minimum price" name="priceFilter"  onClick={(e)=>{setMinPriceFilter(e.target.value)}} />
        <input type="number" placeholder="Maximum price" name="priceFilter"  onChange={(e)=>{setMaxPriceFilter(e.target.value)}} />
        {
          
          data.map( (getD)=>(

            
            <div key={getD.regNumber}>
              <div className = "Car">
              <a href={"/CarDetails/"+getD.regNumber}>{getD.make} {getD.model}</a>
              <br></br>
                <div className="Price"><strong>Price: </strong>{  getD.price  } per day</div>
                <br></br>
                <div className="Picture"><strong></strong><img src={require(`../imgs/${  getD.photo  }`)} width="300" height="215" /> </div>
                <br></br>
                <br></br>
              </div>
              <br>
              
              </br>
                
            
            </div>
            

          )
          
          )
        }

      
      </>
  );}



export default CarsForRent;
