//import './App.css';
import Axios from 'axios';
import DatePicker from 'react-datetime';

import {BrowserRouter as Router, Switch, Route, Link, useNavigate, useParams, useLocation} from 'react-router-dom';
import 'react-datetime/css/react-datetime.css';

function Calendar(props){
    const [ranges, setRanges] = useState([]);
    const [rangeLength,setRangeLength] = useState("");
    const [test,setTest] = useState([]);

    const collect = () =>{
        Axios.post('http://localhost:3001/api/unavailableDates', {
        regNo:  props.regNumber
        }).then((response)=>{    
            // if (response.data == "Successful reservation"){
              setRangeLength(response.data.length);
                setTest(response.data)
              console.log("111")           
              console.log(rangeLength)
            //   for (var i = 0; i < rangeLength; i++) {
              
            //   //const newRange = [response.data[i].start_date.substring(0, 10), response.data[i].end_date.substring(0, 10)];
            //   //const newRanges = [...ranges, newRange];
            //   setRanges(newRanges);
                
            //   }
            
        });    
    }
    const disableCustomDt = date => {
       

        for (let i = 0; i < test.length; i++) {
            // console.log(ranges[i][0]);
          const start = new Date(test[i].start_date);
          const end = new Date(test[i].end_date);
          if (date >= start && date <= end) {
            return false;
          }
        }
        const start = new Date(props.availableStart);
        const end = new Date(props.availableStart);
      if (date < start || date > end) {
          return false;
      }
        return true;
      };
      
    function setValues(){
        // props.loc(document.getElementById("location").value)
        // props.comment(document.getElementById("written").value)
        // props.cond(document.getElementById("condition").value)
    }
    collect()
    return (
        <div >
            <DatePicker
            timeFormat={false}
            isValidDate={disableCustomDt}
            />
            <div>
            {test.map( (getT)=>(
                  
                  <div className="reservationInfo">
                
                   <div> Start date: {getT.start_date.substring(0, 10)} </div>
                   <div> End date: {getT.end_date.substring(0, 10)} </div>

                  
                 </div>
                 
                  
                 ))} 
                 </div> 
        </div>
    ) ;
}

export default Calendar
