import React, {useState, useEffect} from "react"; 
//import './App.css';
import Axios from 'axios';
import {BrowserRouter as Router, Switch, Route, Link, useNavigate, useParams} from 'react-router-dom';

import { useAuth } from "./auth";
 

function RegisterUser() {
  const [username, setUsername] = useState("");
  const [pw, setPW] = useState("");
  const [type, setType] = useState("");

  var navigate = useNavigate();
  const auth = useAuth();

  

  const regUser = () => {
    console.log('handling reg');
    var e = document.getElementById("userType");
  
    setType(e.value);

    console.log(e);
    console.log(e.value);
    console.log(type);

    Axios.post('http://localhost:3001/api/insert', {
        username: username, 
        pw: pw,
        type: type
        }).then((response)=>{
           // alert(response.data)
            
            if (response.data == "Successful registration"){
                if (type == '0'){
                    console.log(' loaner ');
                    auth.login(username, pw)
                    navigate('/loaner-page');
                }
                else{
                    console.log(' renter ');
                    auth.login(username, pw)
                    navigate('/renter-page');
                }
            }
            // if (response.data.length == 0){
            //   alert("reg");
              
            // }
            // else{
            //   console.log("ok logging in")
            //  // auth.login(username, pw)
            // }
           
        });

  

  };


  return (
    
    <div className="App">  
      <h1> RentMyRide </h1>
      <nav>
        <Link to="/"> back </Link>
      </nav>  

      <div className="login">
        <div> Username </div>
        <input 
          type="text" 
          name="user"
          onChange={
            (e) => {setUsername(e.target.value);
          }}
        />

        <div> Password </div>
        <input 
          type="text" 
          name="pw"
          onChange={
            (e) => {setPW(e.target.value);
          }}  
        />

        <div> User Type </div>
        <select id="userType">
            <option value="0" >Loaner</option>
            <option value="1">Renter</option>
            
        </select>
        
        <div> <button onClick={regUser}> Register </button> </div>
    
      </div>

    </div>
  );
}

export default RegisterUser;
