import React, {useState, useEffect} from "react"; 
import "../styles/LoanerLogin.css"
import Axios from 'axios';
import Nav from "./NavBar";
import "../styles/Navbar.css";
import videoBG from "../styles/background9.mp4";

import {BrowserRouter as Router, Switch, Route, Link, useNavigate, useParams} from 'react-router-dom';

import { useAuth } from "./auth";
 

function LoanerLogin() {
  const [user, setUser] = useState('')

  const [username, setUsername] = useState("");
  const [pw, setPW] = useState("");
  let isAuth = false;
  var navigate = useNavigate();
  const auth = useAuth();


  const handleLogin = () => {
    console.log('handling login');

    Axios.post('http://localhost:3001/api/Login', {
        username: username, 
        pw: pw,
        type: 0
        }).then((response)=>{
            if (response.data.length == 0){
              alert("Wrong Password");
              
            }
            else{
              console.log("ok logging in")
              auth.login(username, pw)
              var test = '/loaner-page';
              navigate(test);
            }
           
        });

  

  };


  return (
    <body>

    <video class="Video" src={videoBG} autoPlay loop  playsInline />
     <Nav></Nav> 
    <div class="HomePage">
    
    
    <div class="Appp"> 
 
      <h1> Loaner Login </h1>
    
      <br></br> 

      <div class="login">
        <div> Username </div>
        <input 
          type="text" 
          name="user"
          onChange={
            (e) => {setUsername(e.target.value);
          }}
        />
        <br></br>

        <div> Password </div>
        <input 
          type="password" 
          name="pw"
          required
          onChange={
            (e) => {setPW(e.target.value);
          }}  
        />
        <br></br>
        
        <div> <button onClick={handleLogin}> Login </button> </div>
        <br></br>

        <div class="link2"><a href="/registration">Dont have an account? Register right now</a></div>
    
      </div>

    </div>
    </div>
    </body>
    
  );
}

export default LoanerLogin;
