import React, {useState, useEffect} from "react"; 
//import './App.css';
import Axios from 'axios';


import { useAuth } from "./auth";
import {BrowserRouter as Router, Switch, Route, Link, useNavigate, useParams} from 'react-router-dom';


function CarDetails(props) {

  const { id }  = useParams();

  const [make, setMake] = useState("");
  const [model, setModel] = useState("");
  const [year, setYear] = useState("");
  const [regNumber, setRegNumber] = useState("");
  const [price, setPrice] = useState("");
  const [features, setFeatures] = useState("");
  const [colour, setColour] = useState("");
  const [photo, setPhoto] = useState("");
  const [address, setAddress] = useState("");


  useEffect(() => {
    const info = async () => {
    

      Axios.post("http://localhost:3001/api/Cars", {
      
    }).then((response) => {

      console.log(response.data[1].regNumber);
      console.log(response.data.length)
      for(const car of response.data){
        if (car.regNumber == id){
          setMake(car.make)
          setModel(car.model)
          setYear(car.year)
          setRegNumber(car.regNumber)
          setPrice(car.price)
          setFeatures(car.features)
          setColour(car.colour)
          setPhoto(car.photo)
          setAddress(car.pickupAddress)

        }
      }
      
      
      
      
      
    })}

    info()
    
  })

  return (
      // <Router> 
      <><div>
      
    </div>
      
        <h1> Car Detail Page </h1>

       <div className="Make">Registration Number : {regNumber}</div>
       <div className="Make">Make : {make}</div>
       <div className="Make">Model : {model}</div>
       <div className="Make">Year : {year}</div>
       <div className="Make">Colour : {colour}</div>
       <div className="Make">Features : {features}</div>
       <div className="Make">Pickup Address : {address}</div>
       <div className="Make">Price : {price} per day</div>
        
      </>
  );}



export default CarDetails;
