import React from "react";

function ReviewForm(props){

    function setValues(){
        var loca = document.getElementById("location").value
        console.log(loca)
    }
    
    return (
        <div >
            <div style={{display: "flex", flexDirection:"column", gap:"10px", padding:"10% 10%"}}>
                <div>
                    <label for="location">Location Rating</label>
                    <input type="range" min='0' max='100' id='location' onChange={(e)=>props.loc(e.target.value)}/>
                </div>
                <div>
                    <label for="condition">Condition Rating</label>
                    <input type="range" min='0' max='100' id='condition' onChange={(e)=>props.cond(e.target.value)}/>
                </div>
                <div>
                    <label for="written">Written Comments</label>
                    <input type="textarea" id='written'onChange={(e)=>props.comment(e.target.value)}/>
                </div>
               <button 
                style={{maxWidth:"50px"}} 
                onClick={()=>
                    {setValues();
                    props.sub(true);}
                    
                }
                >
                submit
                </button>
            </div>

        </div>
    ) ;
}

export default ReviewForm
