import React, {useState, useEffect} from "react"; 
//import './App.css';
import Axios from 'axios';

import { useAuth } from "./auth";
import {BrowserRouter as Router, Switch, Route, Link, useNavigate, useParams} from 'react-router-dom';


function CarsForRent() {
  
  const [data, setData] = useState([])

  const [make, setMake] = useState("");
  const [model, setModel] = useState("");
  const [year, setYear] = useState("");
  const [regNumber, setRegNumber] = useState("");
  const [price, setPrice] = useState("");
  const [features, setFeatures] = useState("");
  const [colour, setColour] = useState("");
  const [photo, setPhoto] = useState("image-unknown.png");
  const [address, setAddress] = useState("");
  const [description, setDescription] = useState("");
  const [type, setType] = useState("");

  const [priceFilter, setPriceFilter] = useState("");

  const info = async () => {
    

    Axios.post("http://localhost:3001/api/Cars", {
    
  }).then((response) => {
    setData(response.data)
    for(const car of response.data){
      
      if (car.price == priceFilter){
        console.log("UUUUU")
        setPhoto(car.photo)
        setMake(car.make)
        setModel(car.model)
        setYear(car.year)
        setRegNumber(car.regNumber)
        setPrice(car.price)
        setFeatures(car.features)
        setColour(car.colour)
        setPhoto(car.photo)
        setAddress(car.pickupAddress)
        setType(car.type)
        setDescription(car.description)
        
      }
    }
    
    
    console.log("response")
  })}
  useEffect(() => {
    

    info()
    
  })

  const MoreInfo = () => {
    {info()}
  }
  


  return (
      // <Router> 
      <><div>
      
    </div>
      
        <h1> RentMyRide </h1>
        <h1>{ regNumber }</h1>
        <h1>{ priceFilter }</h1>
        {
          
          data.map( (getD)=>(

            
            <div key={getD.regNumber}>
              <div className = "Car">
              <a href={"/CarDetails/"+getD.regNumber}>{getD.make} {getD.model}</a>
              <br></br>
                <div className="Price"><strong>Price: </strong>{  getD.price  } per day</div>
                <br></br>
                <div className="Picture"><strong></strong><img src={require(`../imgs/${  getD.photo  }`)} width="300" height="215" /> </div>
                <br></br>
                <br></br>
              </div>
              <br>
              
              </br>
                
            
            </div>
            

          )
          
          )
        }

      <input type="text" placeholder="Minimum price" name="priceFilter"  onChange={(e)=>{setPriceFilter(e.target.value)}} />
      <button onClick={MoreInfo}>filter</button>
      </>
  );}



export default CarsForRent;
